CREATE DATABASE IF NOT EXISTS oscar_database; 

-- Escolho o banco para trabalhar
USE oscar_database;

-- Apagar uma tabela 
-- DROP TABLE filmes;
DROP TABLE IF EXISTS indicados_ao_oscar;

-- Alguns exemplos de regras/constraints:
-- PRIMARY KEY     > torna o campo único e obrigatório -> Só pode ser utilizado uma única vez por tabela. 
-- AUTO_INCREMENT  >
-- NOT NULL        > Não Vazio/Obrigatório
-- UNIQUE          > Único
-- DEFAULT         > Se o campo estiver vazio ele preencherá automaticamente com o valor informado ao lado

-- Exemplo de como criar uma Tabela

DROP TABLE IF EXISTS indicados_ao_oscar;
CREATE TABLE IF NOT EXISTS indicados_ao_oscar(
  id_registro       INT PRIMARY KEY AUTO_INCREMENT  
  ,ano_filmagem     INT NOT NULL
  ,ano_cerimonia    INT 
  ,edicao_cerimonia INT NOT NULL 
  ,categoria        VARCHAR(255) NOT NULL
  ,nome_indicado    VARCHAR(512) 
  ,nome_filme       VARCHAR(255) Default "Nome Padrão de um Filme"
  ,vencedor         BOOLEAN NOT NULL
);


